//program to guess a number generated by system!

let number= Math.floor((Math.random() *100)+1)  //generates random whole number between 1 and 100
            
             let unumber;                       //user entered number
             let chance=0;                      
             let score=100;                    
             
             do {
                unumber=parseInt(prompt("Enter the number you guess:"));

               if(unumber>number) {
                  console.log("The number you entered is greater:");
                  chance++;
               
               }
               else if(unumber<number) {
                  console.log("The number you entered is smaller:");
                  chance++;
               }
               else {
                  console.log(`Hurrah!! you guess number ${number} in ${chance} tries`);
                  console.log(`YOUR SCORE IS:`,(score-chance));
               }
             }
             while(unumber !=number) 